import "./App.css";

function App() {
  const names = ["Pedro", "Jake", "Mike", "Dustin"];
  return (
    <div>
      {" "}
      <h1>
        {names.map((value, key) => {
          return <h1 key={key}>{value}</h1>;
        })}
      </h1>
    </div>
  );
  const age = 15;
  const isGreen = true;
  return (
    <div style={{ color: isGreen ? "green" : "red" }}>
      {" "}
      {age >= 18 ? <h1>over age</h1> : <h1>under age</h1>}{" "}
      {isGreen && <button> This is a button</button>}
    </div>
  );
  return (
    <div className="App">
      <Job salary="1" position="senior" company="amazong" />
      <Job salary="2" position="limbo" company="google"></Job>
      <Job salary="3" position="junior" company="facebook"></Job>
    </div>
  );
}

const Job = (x) => {
  return (
    <div>
      <h1>{x.salary}</h1>
      <h1 className="name">{x.position}</h1>
      <h1>{x.company}</h1>
    </div>
  );
};

export default App;
