import { Component, Input, EventEmitter, Output } from '@angular/core';
@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.css'],
})
export class ItemComponent {
  @Input() item: any;
  @Input() editingTodoTitle: any;
  @Output() toggleCompletionEvent = new EventEmitter();
  @Output() editTodoEvent = new EventEmitter();
  @Output() removeTodoEvent = new EventEmitter();
  @Output() updateEditingTodoEvent = new EventEmitter();
  @Output() stopEditingEvent = new EventEmitter();
  toggleCompletion(item) {
    this.toggleCompletionEvent.emit(item);
  }
  editTodo(item) {
    this.editTodoEvent.emit(item);
  }
  removeTodo(item) {
    this.removeTodoEvent.emit(item);
  }
  updateEditingTodo(item) {
    this.updateEditingTodoEvent.emit(item);
  }
  stopEditing(item) {
    this.stopEditingEvent.emit(item);
  }
}
<li
  class="todo-item"
  [class.completed]="item.completed"
  [class.editing]="item.editing"
>
  <!-- NOTE - change to view-todo -->
  <div class="view">
    <!-- NOTE - change to toggle-done -->
    <input
      class="toggle"
      type="checkbox"
      (click)="toggleCompletion(item)"
      [checked]="item.completed"
    />
    <label (dblclick)="editTodo(item)">{{ item.title }}</label>
    <!-- NOTE: Change to remove-todo -->
    <button class="destroy" (click)="removeTodo(item)"></button>
  </div>
  <!-- NOTE: Change to edit-todo -->
  <input
    class="edit"
    type="text"
    placeholder="Exiting todo to edit"
    *ngIf="item.editing"
    [(ngModel)]="editingTodoTitle"
    (keyup.enter)="updateEditingTodo(item)"
    (blur)="stopEditing(item)"
    (keyup.escape)="stopEditing(item)"
  />
</li>
