import { Component, Input } from '@angular/core';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'todomvc';
  allItems: any[] = [];

  //method start
  constructor() {
    const savedTodos = JSON.parse(localStorage.getItem('todoList') || '[]');
    this.allItems = savedTodos.map((todo) => {
      return {
        id: todo.id,
        title: todo.title,
        completed: todo.completed,
        editing: todo.editing,
      };
    });
  }
  //method end

  @Input()
  newTodoText = '';
  editingTodoTitle = '';
  get items() {
    if (this.filter === 'all') {
      return this.allItems;
    }
    return this.allItems.filter((item) =>
      this.filter === 'completed' ? item.completed : !item.completed
    );
  }
  get remaining() {
    return this.allItems.filter((todo) => !todo.completed).length;
  }
  filter: 'all' | 'active' | 'completed' = 'all';
  toggleCompletion(todo) {
    todo.completed = !todo.completed;
    this.updateStore();
  }
  toggleAll() {
    this.allItems.forEach((todo) => (todo.completed = !todo.completed));
    this.updateStore();
  }
  get completed() {
    return this.allItems.filter((todo) => todo.completed);
  }
  removeCompleted() {
    this.allItems = this.allItems.filter((todo) => !todo.completed);
    this.updateStore();
  }
  addTodo() {
    if (this.newTodoText.trim().length) {
      this.allItems.push({
        id: Date.now(),
        title: this.newTodoText,
        completed: false,
        editing: false,
      });
      this.newTodoText = '';
    }
    this.updateStore();
  }
  removeTodo(todo) {
    this.allItems.splice(this.allItems.indexOf(todo), 1);
    this.updateStore();
  }

  editTodo(todo) {
    todo.editing = true;
    this.editingTodoTitle = todo.title;
  }

  updateEditingTodo(todo) {
    //new code start
    if (this.editingTodoTitle.length === 0) {
      return this.removeTodo(todo);
    }
    //new code end
    todo.title = this.editingTodoTitle;
    todo.editing = false;
    this.editingTodoTitle = '';
    this.updateStore();
  }

  stopEditing(todo) {
    todo.editing = false;
  }

  updateStore() {
    localStorage.setItem('todoList', JSON.stringify(this.allItems));
  }
}
<section class="todoapp">
  <header class="header">
    <h1>Todos</h1>
    <input
      autofocus
      class="new-todo"
      type="text"
      placeholder="What needs to be done?"
      [(ngModel)]="newTodoText"
      (keyup.enter)="addTodo()"
    />
  </header>
  <section class="main" *ngIf="allItems.length > 0">
    <input
      class="toggle-all"
      id="toggle-all"
      type="checkbox"
      *ngIf="allItems.length"
      [checked]="remaining === 0"
      (click)="toggleAll()"
    />
    <label for="toggle-all">Mark all as complete</label>
    <ul class="todo-list" *ngFor="let item of items">
      <li
        class="todo-item"
        [class.completed]="item.completed"
        [class.editing]="item.editing"
      >
        <!-- NOTE - change to view-todo -->
        <div class="view">
          <!-- NOTE - change to toggle-done -->
          <input
            class="toggle"
            type="checkbox"
            (click)="toggleCompletion(item)"
            [checked]="item.completed"
          />
          <label (dblclick)="editTodo(item)">{{ item.title }}</label>
          <!-- NOTE: Change to remove-todo -->
          <button class="destroy" (click)="removeTodo(item)"></button>
        </div>
        <!-- NOTE: Change to edit-todo -->
        <input
          class="edit"
          type="text"
          placeholder="Exiting todo to edit"
          *ngIf="item.editing"
          [(ngModel)]="editingTodoTitle"
          (keyup.enter)="updateEditingTodo(item)"
          (blur)="stopEditing(item)"
          (keyup.escape)="stopEditing(item)"
        />
      </li>
      <app-item></app-item>
    </ul>
  </section>
  <footer class="footer" *ngIf="allItems.length > 0">
    <span class="todo-count">
      <strong>{{ remaining }}</strong>
      <span>{{ remaining == 1 ? "item" : "items" }} left</span>
    </span>
    <ul class="filters">
      <li>
        <a href="#/all" (click)="filter = 'all'">All</a>
      </li>
      <li>
        <a href="#/active" (click)="filter = 'active'">Active</a>
      </li>
      <li>
        <a href="#/completed" (click)="filter = 'completed'">Completed</a>
      </li>
    </ul>
    <button
      class="clear-completed"
      *ngIf="completed.length > 0"
      (click)="removeCompleted()"
    >
      Clear completed todos
    </button>
  </footer>
</section>
