import { Component } from '@angular/core';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'todomvc';
  allItems = [
    {
      id: Date.now(),
      title: 'Todo Item',
      completed: false,
    },
  ];
  get items() {
    if (this.filter === 'all') {
      return this.allItems;
    }
    return this.allItems.filter((item) =>
      this.filter === 'completed' ? item.completed : !item.completed
    );
  }
  get remaining() {
    return this.allItems.filter((todo) => !todo.completed).length;
  }
  filter: 'all' | 'active' | 'completed' = 'all';

  toggleCompletion(todo: any) {
    todo.completed = !todo.completed;
  }

  toggleAll() {
    this.allItems.forEach((todo) => (todo.completed = !todo.completed));
  }

  get completed() {
    return this.allItems.filter((todo) => todo.completed);
  }

  removeCompleted() {
    this.allItems = this.allItems.filter((todo) => !todo.completed);
  }
}
<section class="todoapp">
  <header class="header">
    <h1>Todos</h1>
    <input
      autofocus
      class="new-todo"
      type="text"
      placeholder="What needs to be done?"
    />
  </header>
  <section class="main" *ngif="allItems.length > 0">
    <input
      class="toggle-all"
      id="toggle-all"
      type="checkbox"
      checked="false"
      *ngIf="allItems.length"
      [checked]="remaining === 0"
      (click)="toggleAll()"
    />
    <label for="toggle-all">Mark all as complete</label>
    <ul class="todo-list" *ngFor="let item of items">
      <li class="todo-item" [class.completed]="item.completed">
        <!-- NOTE - change to view-todo -->
        <div class="view">
          <!-- NOTE - change to toggle-done -->
          <input
            class="toggle"
            type="checkbox"
            (click)="toggleCompletion(item)"
            [checked]="item.completed"
          />
          <label> {{ item.title }} </label>
          <!-- NOTE: Change to remove-todo -->
          <button class="destroy"></button>
        </div>
        <!-- NOTE: Change to edit-todo -->
        <!-- <input class="edit" type="text" placeholder="Exiting todo to edit"> -->
      </li>
    </ul>
  </section>
  <footer class="footer">
    <span class="todo-count">
      <strong>{{ remaining }}</strong>
      <br />
      <span>{{ remaining == 1 ? "item" : "items" }} left</span>
    </span>
    <ul class="filters">
      <li>
        <a href="#/all" (click)="filter = 'all'">All</a>
      </li>
      <li>
        <a href="#/active" (click)="filter = 'active'">Active</a>
      </li>
      <li>
        <a href="#/completed" (click)="filter = 'completed'">Completed</a>
      </li>
    </ul>
    <button
      class="clear-completed"
      *ngIf="completed.length > 0"
      (click)="removeCompleted()"
    >
      Clear completed todos
    </button>
  </footer>
</section>
